import * as React from 'react';
import styles from './Summary.module.scss';
import summaryHeader from '../../resources/jsons/summaryHeader.json';
import summaryData from '../../resources/jsons/summaryData.json';

export default (class Summary extends React.PureComponent {
	state = {};

    componentDidMount() {}

    calculateFooter = (data, item) => {
		switch (item.footer) {
			case 'sum':
				return data.reduce((acc, row) => (acc += parseFloat(row[item.value])), 0);
			default:
				return item.footer;
		}
	};

	render() {
        const headers = summaryHeader;
		const data = summaryData[0].summary;
		return (
			<div className={styles.main}>
                {data.map((header, i) => {
                    return (
				<table key= {i} className={styles.table}>
					<thead className={styles.mainHeader}>
						<tr className={styles.header}>
						{headers.map((header, i) => {
								return (
									<th key={i} className={styles.header_item}>
										{header.name}
									</th>
								);
							})}
						</tr>
					</thead>
					<tbody className={styles.body}>
						{data.map((item, i) => {
							return (
								<tr key={i} className={styles.row}>
									{headers.map((header, i) => {
										return <td className={styles.row_item}>{item[header.value]}</td>;
									})}
								</tr>
							);
						})}
					</tbody>
					<tfoot className={styles.footer}>
						<tr className={styles.footer_row}>
							{headers.map((header, i) => {
								return <td className={styles.footer_item}>{this.calculateFooter(data, header)}</td>;
							})}
						</tr>
					</tfoot>
				</table>
                );
                })}
			</div>
		);
	}

});
